<span class="line"><span style="color:#E1E4E8;">            </span><span style="color:#B392F0;">begin</span><span style="color:#E1E4E8;">=</span><span style="color:#9ECBFF;">&quot;0s&quot;</span><span style="color:#E1E4E8;"> </span><span style="color:#B392F0;">dur</span><span style="color:#E1E4E8;">=</span><span style="color:#9ECBFF;">&quot;10s&quot;</span><span style="color:#E1E4E8;"> </span><span style="color:#B392F0;">repeatCount</span><span style="color:#E1E4E8;">=</span><span style="color:#9ECBFF;">&quot;indefinite&quot;</span></span>
